import React from "react"
import './Cartao.css'


const Cartao = props => {

    const estilo = props => {
        if (props.assentos == 0) {
            return `rgb(255,0,0)`
        } else {
            return `rgb(0,128,0)`
        }

        
    }
    return (
        <div className="Cartao" style={estilo}>
            <div className="Titulo">{props.titulo}</div>     
            <div className="Conteudo">{props.children}</div>  
        </div>
          
    )
}
export default Cartao
