        }
        else {
            return `rgb(0, 128, 0)`
        }
    }

    const estilo = {
        backgroundColor: cor1 || 'gray',
        borderColor: cor || 'black',
    }
    return (
        <div className="Cartao" style={estilo}>
            <div className="Titulo">{props.titulo}</div>     
            <div className="Conteudo">{props.children}</div>  
